import React, {PureComponent} from 'react'
import {withStyles} from '@material-ui/core/styles'
import {connect} from 'react-redux'
import Dicomdir from './components/Dicomdir'
import DicomViewer from './components/DicomViewer'
import DicomHeader from './components/DicomHeader'
import Measurements from './components/Measurements'
import Settings from './components/Settings'
import AboutDlg from './components/AboutDlg'
import Histogram from './components/Histogram'
import LayoutTool from './components/LayoutTool'
import FsUI from './components/FsUI'
import DownloadZipDlg from './components/DownloadZipDlg'
import OpenMultipleFilesDlg from './components/OpenMultipleFilesDlg'
import AppBar from '@material-ui/core/AppBar'
import Button from '@material-ui/core/Button'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import Divider from '@material-ui/core/Divider'
import Drawer from '@material-ui/core/Drawer'
import ExpandLess from '@material-ui/icons/ExpandLess'
import ExpandMore from '@material-ui/icons/ExpandMore'
import Icon from '@mdi/react'
import IconButton from '@material-ui/core/IconButton'
import List from '@material-ui/core/List'
import ListItem from '@material-ui/core/ListItem'
import ListItemIcon from '@material-ui/core/ListItemIcon'
import ListItemText from '@material-ui/core/ListItemText'
import Snackbar from '@material-ui/core/Snackbar'
import TextField from '@material-ui/core/TextField'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
//import { FixedSizeList } from 'react-window'
//import {List as ListVirtual} from 'react-virtualized'
import SeriesItem from './components/SeriesItem'
import {
  isMobile,
  isTablet,
} from 'react-device-detect'
import {
  clearStore,
  localFileStore,
  dcmIsOpen,
  activeDcm,
  activeDcmIndex,
  activeMeasurements,
  setLayout,
  dcmTool,
  setDicomdir,
  setZippedFile,
  setVolume,
  filesStore,
} from './actions/index'
import {
  log,
  getPixelSpacing,
  getSpacingBetweenSlice,
  getSliceThickness,
  getSliceLocation,
  //getFileNameCorrect,
  getFileExtReal,
  isInputDirSupported,
  getSettingsFsView,
  getSettingsDicomdirView,
  getSettingsMprInterpolation,
} from './functions'
import {
  mdiAngleAcute,
  mdiArrowAll,
  mdiAxisArrow,
  mdiCamera,
  mdiChartHistogram,
  mdiCheck,
  mdiCheckboxIntermediate,
  mdiContentSaveOutline,
  mdiCursorDefault,
  mdiCursorPointer,
  mdiDelete,
  mdiEllipse,
  mdiEyedropper,
  mdiFileCabinet,
  mdiFileDocument,
  mdiFileCad,
  mdiFolder,
  mdiFolderMultiple,
  mdiGesture,
  mdiViewGridPlusOutline,
  mdiImageEdit,
  mdiInformationOutline,
  mdiInvertColors,
  mdiMagnify,
  mdiFolderOpen,
  mdiRefresh,
  mdiRectangle,
  mdiRuler,
  mdiSettings,
  mdiToolbox,
  mdiTools,
  mdiTrashCanOutline,
  mdiVideo,
  mdiWeb,

  mdiPlay,
  mdiPause,
  mdiSkipBackward,
  mdiSkipForward,
  mdiSkipNext,
  mdiSkipPrevious, mdiArrowLeft, mdiArrowLeftCircle, mdiViewList,
} from '@mdi/js'

import './App.css'
import Tooltip from "@material-ui/core/Tooltip";
import MenuItem from "@material-ui/core/MenuItem";
import Menu from "@material-ui/core/Menu";
import Popper from "@material-ui/core/Popper";
import Grow from "@material-ui/core/Grow";
import Paper from "@material-ui/core/Paper";
import ClickAwayListener from "@material-ui/core/ClickAwayListener";
import MenuList from "@material-ui/core/MenuList";
import Card from "@material-ui/core/Card";
import * as cornerstoneWADOImageLoader from "cornerstone-wado-image-loader";
import * as cornerstone from "cornerstone-core";
import * as dicomParser from "dicom-parser";
import SeriesList from "./components/SeriesList";

// log();

localStorage.setItem("debug", "cornerstoneTools");

const drawerWidth = 240;
const iconColor = '#FFFFFF';
let iconTool = null;

const styles = theme => ({
  '@global': {
    body: {
      backgroundColor: theme.palette.common.black,
    },
  },

  grow: {
    flexGrow: 1,
  },

  root: {
    display: 'flex',
  },

  menuButton: {
    marginRight: theme.spacing(2),
  },

  title: {
    flexGrow: 1,
    textAlign: 'center'
  },

  appBar: {
    position: 'relative',
    backgroundColor: '#453479',
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },

  toolbarCard: {
    zIndex: theme.zIndex.drawer + 1,
    backgroundColor: '#453479',
    position: 'absolute',
    margin: '0 25% 0 25%',
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    top: 'auto',
