  };


  toggleDicomdir = () => {
    const visible = !this.state.visibleDicomdir;
    console.log('toggleDicomdir: ', visible);
    this.setState({visibleDicomdir: visible});
    if (visible)
      this.setState({
        visibleMeasure: false,
        visibleToolbox: false,
        visibleHeader: false,
