            setTimeout(() => {
              this.mprBuildVolume()
            }, 100)
          })
        } else {
          this.changeToCoronalView()
        }
      })
    }
  };

  mprAxial = () => {
    const visibleMprAxial = this.state.visibleMprAxial;
    if (!visibleMprAxial) {
