  //     });
  //     this.setState({seriesItemView: _seriesItemView});
  //   }
  // }

  render() {
    let seriesItemView = this.state.seriesItemView;
    return (
      <ListItem>
        {seriesItemView.length > 0 ?
          seriesItemView : (<p style={{padding: 0, margin: '5px 10px 5px 10px'}}>nothing</p>)
        }
      </ListItem>
    );
  }
}
